import React, {useState} from "react";
// import { MDBContainer, MDBTypography } from "mdb-react-ui-kit";
import {MDBValidation, MDBInput, MDBBtn} from "mdb-react-ui-kit";
import { useNavigate, useParams } from "react-router-dom";
import axios from 'axios';
import {toast} from "react-toastify"
import { useEffect } from "react/cjs/react.development";

//xqvmv0yi

const initialState = {
likes: "",
comments: "",
authorname: "",
likes: "",
title: "",
description: "",
category: "",
imageUrl: ""
}

const options = ['Travel', "Fashion", "Fitness", "Sports", "Food", "Tech"]
const AddEditBlog = () => {
    const [formValue, setFormValue] = useState(initialState);
    const [categoryErrMsg, setCategoryErrMsg] = useState(null);
    const {authorname, title, description, likes, comments, category, imageUrl} = formValue; 
    const [editMode, setEditMode] = useState(false);

    const navigate = useNavigate();

    const { id } = useParams();

  useEffect(() => {
    if (id) {
      setEditMode(true);
      getSingleBlog(id);
    } else {
      setEditMode(false);
      setFormValue({ ...initialState });
    }
  }, [id]);

  const getSingleBlog = async (id) => {
    const singleBlog = await axios.get(`http://localhost:5000/blogs/${id}`);
    if (singleBlog.status === 200) {
      setFormValue({ ...singleBlog.data });
    } else {
      toast.error("Something went wrong");
    }
  };

    const getDate = () => {
        let today = new Date();
        let dd = String(today.getDate()).padStart(2, "0");
        let mm = String(today.getMonth() + 1).padStart(2, "0"); //January is 0!
        let yyyy = today.getFullYear();

        today = mm + "/" + dd + "/" + yyyy;
        return today;
      };
      const handleSubmit = async (e) => {
        e.preventDefault();
        if (!category) {
          setCategoryErrMsg("Please select a category");
        }
        const imageValidation = !editMode ? imageUrl : true;
        if (authorname && title && description && imageUrl && category && likes && comments) {
          const currentDate = getDate();
          if(!editMode){
            const updatedBlogData = { ...formValue, date: currentDate };
            const response = await axios.post(
                "http://localhost:5000/blogs",
                updatedBlogData
              );
              if (response.status === 201) {
                toast.success("Blog Created Successfully");
              } else {
                toast.error("Something went wrong");
              }

          }
            else{
                const response = await axios.put(
                    `http://localhost:5000/blogs/${id}`,
                    formValue
                  );
                  if (response.status === 200) {
                    toast.success("Blog Created Successfully");
                  } else {
                    toast.error("Something went wrong");
                  }
            }


          setFormValue({ authorname: "", title: "", description: "", likes: "", comments: "", category: "", imageUrl: "" });
          navigate("/");
        }
      };
    const onInputChange = (e) => {
        let { name, value } = e.target;
        setFormValue({ ...formValue, [name]: value });
    };
    const onUploadImage = (file) => {
        const formData = new FormData();
        formData.append("file", file);
        formData.append("upload_preset", "xqvmv0yi");
        axios
        .post("http://api.cloudinary.com/v1_1/ddy1g5aim/image/upload", formData)
        .then((resp) => {
          toast.info("Image Uploaded Successfully");
          setFormValue({ ...formValue, imageUrl: resp.data.url });
        })
        .catch((err) => {
          toast.error("Something went wrong");
        });
    };
    const onCategoryChange = (e) => {
        setCategoryErrMsg(null);
        setFormValue({ ...formValue, category: e.target.value });
      };

  return (
    // <div style={{ marginTop: "100px" }}>
    //   {/* <MDBContainer>
    //     <MDBTypography note noteColor="primary">
    //       It is blogging website where you will find blog post related to
    //       different category like Travel, Food, Sports Fitness, Tech and Fashion
    //     </MDBTypography>
    //   </MDBContainer> */}
    //   <h2>Add Edit</h2>
    // </div>
    <MDBValidation className= "row g-3" style = {{marginTop: '100px'}} noValidate onSubmit={handleSubmit}>
         <p className="fs-2 fw-bold">{editMode ? "Update Blog" : "Add Blog"}</p>
        <div
        style={{
          margin: "auto",
          padding: "15px",
          maxWidth: "400px",
          alignContent: "center",
        }}
      >
                  <MDBInput
          value={authorname || ""}
          name="authorname"
          type="text"
          onChange={onInputChange}
          required
          label="Author Name"
          validation="Please provide an Author Name"
          invalid
        />
        <br/>
           <MDBInput
          value={title || ""}
          name="title"
          type="text"
          onChange={onInputChange}
          required
          label="Title"
          validation="Please provide a title"
          invalid
        />
        <br/>
          <MDBInput
          value={likes || ""}
          name="likes"
          type="text"
          onChange={onInputChange}
          required
          label="Number of Likes"
          validation="Please Enter the number of likes"
          invalid
        />
        <br/>
         <MDBInput
          value={comments || ""}
          name="comments"
          type="text"
          onChange={onInputChange}
          required
          label="Comments"
          validation="Please Enter the number of comments recieved"
          invalid
        />
         <br />
         <MDBInput
          value={description || ""}
          name="description"
          type="text"
          onChange={onInputChange}
          required
          label="Description"
          validation="Please provide a description"
          textarea
          rows={4}
          invalid
        />
        <br />
        {!editMode && (
          <>
            <MDBInput
              type="file"
              onChange={(e) => onUploadImage(e.target.files[0])}
              required
              validation="Please provide a title"
              invalid
            />
            <br />
          </>
        )}
             <select
          className="categoryDropdown"
          onChange={onCategoryChange}
          value={category}
        >
          <option>Please select category</option>
          {options.map((option, index) => (
            <option value={option || ""} key={index}>
              {option}
            </option>
          ))}
        </select>
        {categoryErrMsg && (
          <div className="categoryErrorMsg">{categoryErrMsg}</div>
        )}
        <br />
        <br />
        <MDBBtn type="submit" style={{ marginRight: "10px" }}>
          {editMode ? "Update" : "Add"}
        </MDBBtn>
        <MDBBtn
          color="danger"
          style={{ marginRight: "10px" }}
          onClick={() => navigate("/")}
        >
          Go Back
        </MDBBtn>
      </div>
        </MDBValidation>
  );
};

export default AddEditBlog;
